import React from 'react';
import './App.css';

// Generate a random code verifier
function generateCodeVerifier(length) {
  const characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789-._~';
  let verifier = '';
  for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      verifier += characters[randomIndex];
  }
  return verifier;
}

const client_id = '041aaa9e068b55ee57358403438e841f';
const API_URL = 'https://myanimelist.net/v1/oauth2/authorize?response_type=code&client_id=' + client_id + '&code_challenge=' + generateCodeVerifier(50);




function App() {
// Create a state variable for the response data
const [data, setData] = useState(null);

// Fetch the data when the component mounts
useEffect(() => {
  fetch(API_URL)
    .then(response => response.json())
    .then(data => setData(data))
    .catch(error => console.error('Error:', error));
}, []);
  return (
    <div >
      <header >

        <p>
          props.data
        </p>

      </header>
    </div>
  );
}

export default App;
