import React, { useState, useEffect } from "react";
import './App.css';

const API_URL = "https://api.jikan.moe/v4/anime"



function App() {

  const [searchTerm, setSearchTerm] = useState("");
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    searchMovies("Naurto");
  }, []);

  const searchMovies = async (title) => {
    const response = await fetch(`${API_URL}?q=${title}`);
    const data = await response.json();

    setMovies(data.Search);
  };

  formEl.addEventListener('submit', async(e) => {
    // prevent default form behaviour
     e.preventDefault()

    let searchString = searchEl.value;
  try {
    const res = await fetch(`https://api.jikan.moe/v4/anime?q=${searchString}`);
    const data = await res.json();
    console.log(data)

    const truncatedSynopsis = truncateText(data.data[0].synopsis, 300);
    data.data.synopsis = truncatedSynopsis;
    const animeCardHTML = generateAnimeCard(data);
    animeEl.innerHTML = animeCardHTML;
  } catch (err) {
    console.log(err);
    animeEl.innerHTML = `<p>${err}</p>`;
  }
})


  return (
    <div className="app">
    <h1>MovieLand</h1>

    <div className="search">
      <input
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search for movies"
      />
     <img
          src="SearchIcon.webp"
          alt="search"
          height="300" width="249" 
          onClick={() => searchMovies(searchTerm)}
        />

    </div>

    {movies?.length > 0 ? (
      <div className="container">

      </div>
    ) : (
      <div className="empty">
        <h2>No movies found</h2>
      </div>
    )}
  </div>
  );
}

export default App;
